mapper.select:
select a.* from article as a
left join article_tag_ref as b on a.id = b.article_id left join tag as c on b.tag_id = c.id
and c.id in
${tagId}
and a.category_id = ${articleQueryDTO.categoryId}
and a.title like CONCAT('%',#{articleQueryDTO.keyword},'%')
and a.is_published = ${articleQueryDTO.isPublished}
group by a.id order by a.create_time desc

select
    a.id , a.title , a.is_published , a.is_recommend , a.is_appreciation , a.is_comment_enabled ,
    a.create_time , a.update_time , a.views , a.words , a.is_top , c.name category_name
from
    article as a left join category as c on a.category_id = c.id


        and a.category_id = ${queryParam.categoryId}


        and a.is_top = ${queryParam.isTop}


        and a.is_published = ${queryParam.isPublished}


        and a.is_recommend = ${queryParam.isRecommend}


        and a.is_appreciation = ${queryParam.isAppreciation}


        and a.is_comment_enabled = ${queryParam.isCommentEnabled}


        and a.create_time between #{queryParam.startTime} and #{queryParam.endTime}


        and a.title like CONCAT('%',#{queryParam.keyword},'%')


order by a.create_time desc
