mapper.select:
select id, nickname, email, content, avatar, create_time, ip, is_published, website, qq, is_admin_comment, page, reply_nickname, article_id, origin_id, parent_comment_id from comment
and page = ${queryCond.page}
and article_id = ${queryCond.articleId}
and parent_comment_id = -1 and is_published = true
order by create_time desc
