mapper.select:
select id, title, author, catalog_id, music_url, lrc_url, pic_url,
       is_published, create_time, update_time
from music


        and catalog_id = #{queryCond.catalogId}


        and title like concat('%',#{queryCond.keyword},'%')


        and is_published = #{queryCond.isPublished}


        and create_time between #{queryCond.startTime} and #{queryCond.endTime}


order by create_time desc
